@import 'tailwindcss/base';
@import 'tailwindcss/components';
@import 'tailwindcss/utilities';

/* Custom brand colors */
:root {
  --brand-blue: #0000FF;
  --brand-yellow: #FFBF00;
  --brand-magenta: #FF00FF;
  --brand-cyan: #00FFFF;
  --brand-sage: #DCDFD5;
}

/* Custom utility classes */
.brand-blue { color: var(--brand-blue); }
.bg-brand-blue { background-color: var(--brand-blue); }
.brand-yellow { color: var(--brand-yellow); }
.bg-brand-yellow { background-color: var(--brand-yellow); }
.brand-magenta { color: var(--brand-magenta); }
.bg-brand-magenta { background-color: var(--brand-magenta); }
.brand-cyan { color: var(--brand-cyan); }
.bg-brand-cyan { background-color: var(--brand-cyan); }
.brand-sage { color: var(--brand-sage); }
.bg-brand-sage { background-color: var(--brand-sage); }

/* Gradient backgrounds */
.gradient-brand {
  background: linear-gradient(135deg, var(--brand-blue), var(--brand-magenta));
}

.gradient-accent {
  background: linear-gradient(135deg, var(--brand-yellow), var(--brand-cyan));
}

/* Animation classes */
.animate-fade-in {
  animation: fadeIn 0.5s ease-in;
}

.animate-slide-up {
  animation: slideUp 0.3s ease-out;
}

@keyframes fadeIn {
  from { opacity: 0; }
  to { opacity: 1; }
}

@keyframes slideUp {
  from { 
    opacity: 0; 
    transform: translateY(20px); 
  }
  to { 
    opacity: 1; 
    transform: translateY(0); 
  }
}

/* Custom scrollbar */
.custom-scrollbar::-webkit-scrollbar {
  width: 8px;
}

.custom-scrollbar::-webkit-scrollbar-track {
  background: #f1f1f1;
  border-radius: 4px;
}

.custom-scrollbar::-webkit-scrollbar-thumb {
  background: #c1c1c1;
  border-radius: 4px;
}

.custom-scrollbar::-webkit-scrollbar-thumb:hover {
  background: #a8a8a8;
}

/* Loading spinner */
.spinner {
  width: 40px;
  height: 40px;
  border: 4px solid #f3f3f3;
  border-top: 4px solid var(--brand-blue);
  border-radius: 50%;
  animation: spin 1s linear infinite;
}

@keyframes spin {
  0% { transform: rotate(0deg); }
  100% { transform: rotate(360deg); }
}

/* File upload styles */
.file-upload-area {
  border: 2px dashed #d1d5db;
  border-radius: 12px;
  transition: all 0.3s ease;
}

.file-upload-area.dragover {
  border-color: var(--brand-blue);
  background-color: rgba(0, 0, 255, 0.05);
}

.file-upload-area:hover {
  border-color: #9ca3af;
}

/* Progress bar */
.progress-bar {
  background: linear-gradient(90deg, var(--brand-blue), var(--brand-cyan));
  transition: width 0.3s ease;
}

/* Rich text editor styles */
.ql-toolbar {
  border-top: 1px solid #e5e7eb;
  border-left: 1px solid #e5e7eb;
  border-right: 1px solid #e5e7eb;
  border-radius: 8px 8px 0 0;
}

.ql-container {
  border-bottom: 1px solid #e5e7eb;
  border-left: 1px solid #e5e7eb;
  border-right: 1px solid #e5e7eb;
  border-radius: 0 0 8px 8px;
  font-family: inherit;
}

.ql-editor {
  min-height: 200px;
  font-size: 16px;
  line-height: 1.6;
}

/* Modal styles */
.modal-overlay {
  background-color: rgba(0, 0, 0, 0.6);
  backdrop-filter: blur(4px);
}

.modal-content {
  max-height: 90vh;
  overflow-y: auto;
}

/* Button variants */
.btn-primary {
  background: var(--brand-blue);
  color: white;
  border: 2px solid var(--brand-blue);
  transition: all 0.3s ease;
}

.btn-primary:hover {
  background: transparent;
  color: var(--brand-blue);
}

.btn-secondary {
  background: transparent;
  color: var(--brand-blue);
  border: 2px solid var(--brand-blue);
  transition: all 0.3s ease;
}

.btn-secondary:hover {
  background: var(--brand-blue);
  color: white;
}

/* Audio player styles */
.audio-player {
  background: linear-gradient(135deg, #f8fafc, #e2e8f0);
  border-radius: 12px;
  padding: 16px;
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.05);
}

.audio-controls {
  display: flex;
  align-items: center;
  gap: 12px;
}

.audio-progress {
  flex: 1;
  height: 6px;
  background: #e2e8f0;
  border-radius: 3px;
  overflow: hidden;
}

.audio-progress-bar {
  height: 100%;
  background: var(--brand-blue);
  transition: width 0.1s ease;
}

/* Card hover effects */
.card-hover {
  transition: all 0.3s ease;
}

.card-hover:hover {
  transform: translateY(-4px);
  box-shadow: 0 20px 25px -5px rgba(0, 0, 0, 0.1), 0 10px 10px -5px rgba(0, 0, 0, 0.04);
}

/* Genre-specific colors */
.genre-ebook {
  background: linear-gradient(135deg, var(--brand-blue), #4f46e5);
}

.genre-novel {
  background: linear-gradient(135deg, var(--brand-magenta), #8b5cf6);
}

.genre-kids {
  background: linear-gradient(135deg, var(--brand-yellow), #f59e0b);
}

.genre-coloring {
  background: linear-gradient(135deg, var(--brand-cyan), #06b6d4);
}

/* Responsive design helpers */
@media (max-width: 768px) {
  .modal-content {
    margin: 20px;
    max-height: calc(100vh - 40px);
  }

  .ql-toolbar {
    padding: 8px;
  }

  .ql-toolbar .ql-formats {
    margin-right: 8px;
  }
}

/* Print styles */
@media print {
  .no-print {
    display: none !important;
  }

  .print-break {
    page-break-before: always;
  }
}

/* Focus styles for accessibility */
.focus-visible:focus {
  outline: 2px solid var(--brand-blue);
  outline-offset: 2px;
}

/* Disabled state */
.disabled {
  opacity: 0.5;
  pointer-events: none;
  cursor: not-allowed;
}

/* Success/Error states */
.state-success {
  color: #10b981;
  background-color: #d1fae5;
  border-color: #34d399;
}

.state-error {
  color: #ef4444;
  background-color: #fee2e2;
  border-color: #f87171;
}

.state-warning {
  color: #f59e0b;
  background-color: #fef3c7;
  border-color: #fbbf24;
}

